import PageTItle from "../../layouts/PageTitle";
import exer1 from '../../../images/big/img2.jpg'
import exer2 from '../../../images/big/img7.jpg'
import axios from 'axios';
// import Autocomplete from 'react-autocomplete';
import { useHistory, useParams } from 'react-router-dom';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import { storage } from '../../../firebase';
import { ref,uploadBytes} from 'firebase/storage';

const AddMuscles=()=>{
    const CatAndMusAddition=(name)=>{
        if(name=='categories'){
        axios.post(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Categories.json`,JSON.stringify(cat))
        .then((res)=>{
          alert('New category submitted successfully....');
        setCat({category:''});
       })
       .catch((err)=>alert(err));
      }else if(name=="muscles"){
        axios.post(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Muscles.json`,JSON.stringify(mus))
        .then((res)=>{
          alert('New muscle submitted successfully....');
        setMus({muscle:''});
       })
       .catch((err)=>alert(err));
      }else if(name=="resources"){
         axios.post(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Resources.json`,JSON.stringify(res))
         .then((res)=>{
           alert('New muscle submitted successfully....');
         setRes({resource:''});
        })
        .catch((err)=>alert(err));
       }
      }

    return(
            <Fragment>
               <PageTItle activeMenu="New Exercise" motherMenu="Form" />
      
               <div className="row">
               <div className="card w75">
                        <div className="card-header">
                           <p className='title-descrip'>Here is a brief overview of activities from your clients.</p>
                        </div>
             {  modifyData?(<div className="card-body colTwo6">
                  <div className="col-xl-12 col-lg-12">
                           <div className="basic-form">
                              <form onSubmit={(e) => e.preventDefault()}>
                                 <div className="form-group">
                                 <p className='input-label'>Name</p>
                                    <input
                                       type="text"
                                       name='name'
                                       value={modifyData.name}
                                       onChange={(e)=>setModifyData({...modifyData,name:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="name"
                                    />
                                 </div>

                               <button className='mtb-3 submit-btn'  onClick={(e)=>CatAndMusAddition('muscles')}>Submit</button>
                              </form>
                           </div>
                        </div>
                     </div>):(<div className="card-body colTwo6">
                  <div className="col-xl-12 col-lg-12">
                           <div className="basic-form">
                              <form onSubmit={(e) => e.preventDefault()}>
                                 <div className="form-group">
                                 <p className='input-label'>Name</p>
                                    <input
                                       type="text"
                                       name='name'
                                       value={data.name}
                                       onChange={(e)=>setData({...data,name:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="name"
                                    />
                                 </div>
                               <button className='mtb-3 submit-btn' onClick={(e)=>CatAndMusAddition('muscles')}>Submit</button>
                              </form>
                           </div>
                        </div>
                     </div>)
}
                  </div>
                  </div>
                  </Fragment>
    )
}


export default AddMuscles;
