import React, { Fragment } from 'react';
import PageTItle from "../../layouts/PageTitle";
import deleteIcon from '../../../images/icon/del.png'
import axios from 'axios';
import { useHistory, useParams} from 'react-router-dom';
import { id } from 'chartjs-plugin-streaming';


const AddNewRoutine=()=>{
   const buttonRef = React.useRef();
   const [dis,setDis]=React.useState();
   const [routineData,setRoutineData]=React.useState({name:'',day:'',data:[]});
   const [formEle,setFormEle]=React.useState({exercise:'',series:'',repetitions:'',duration:'',rest:''});
   const [modifyRoutine,setModifyRoutine]=React.useState();
   const history = useHistory();
   const params =useParams();
   const {id}=params;
   const addHandler=(e)=>{
      if(modifyRoutine){
     if(formEle.exercise && formEle.series && formEle.repetitions && formEle.duration && formEle.rest){
       modifyRoutine.data.push(formEle);
       console.log('data',modifyRoutine.data);
      setFormEle({exercise:'',series:'',repetitions:'',duration:'',rest:''});
      setDis();
     }else{
    alert('fill the fields properly...');
     }
   }else{
      if(formEle.exercise && formEle.series && formEle.repetitions && formEle.duration && formEle.rest){
         routineData.data.push(formEle);
         console.log('data',routineData.data);
        setFormEle({exercise:'',series:'',repetitions:'',duration:'',rest:''});
        setDis();
       }else{
      alert('fill the fields properly...');
       }
   }
   }

const submitHandler =(e)=>{
e.target.preventDefault;
if(modifyRoutine){
if(modifyRoutine.name && modifyRoutine.day){
if(modifyRoutine.data.length!=0){
   axios.put(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Routines/${id}.json`,JSON.stringify(modifyRoutine))
   .then((res)=>{
      alert('submit successfully....');
      history.push('/table-routinetable-basic');
   })
   .catch((err)=>alert(err));

}else{
   alert('kindly add atleast one exercise in routine');
}


}else {
   alert('Fill name and day field properly....')
}
}else{
   if(routineData.name && routineData.day){
      if(routineData.data.length!=0){
         axios.post('https://fitnessapp-607ec-default-rtdb.firebaseio.com/Routines.json',JSON.stringify(routineData))
         .then((res)=>{
            alert('submit successfully....');
            setRoutineData({name:'',day:'',data:[]});
         })
         .catch((err)=>alert(err));
      
      }else{
         alert('kindly add atleast one exercise in routine');
      }
      
      }else {
         alert('Fill name and day field properly....')
      }
}
}


const deleteHandler=(index)=>{
   if(modifyRoutine){
  setDis(index);
modifyRoutine.data.splice(index,1);
   }else{
      setDis(index);
routineData.data.splice(index,1);
   }
}
const fetchData=async(id)=>{
var resRoutine =await axios.get(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Routines/${id}.json`);
var resultRoutine=resRoutine.data;
setModifyRoutine(resultRoutine);
console.log(modifyRoutine);
}

React.useEffect(async()=>{
   if(window.location.href.includes('/modify-routine')){
fetchData(id);
   }else{
console.log('hello');
   }
