                                    <input
                                       type="text"
                                       name='duration'
                                       value={formEle.duration}
                                       onChange={(e)=>setFormEle({...formEle,duration:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="Duracion"
                                    />
                                 </div>
                                 <div className="form-group">
                                    <input
                                       type="text"
                                       name='rest'
