import React, { Fragment } from 'react';
import PageTItle from "../../layouts/PageTitle";
import userImg from '../../../images/avatar/8.jpg'
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';
import { storage } from '../../../firebase';
import { ref,uploadBytes,getDownloadURL} from 'firebase/storage';

const NewClientForm=()=>{
   const [modifyData,setModifyData]=React.useState();
   const [data,setData] = React.useState({name:'',dob:'',membershipDate:'',email:'',password:'',routine:'',startDate:'',endDate:'',objName:'',objValue:'',pic:''});
   const [img, setImg] = React.useState(userImg);
   const [imgUpload, setImgUpload] = React.useState();
   const history =useHistory();
   const params =useParams();
   const {id}=params;

   const submitHandler=(e)=>{
      e.target.preventDefault;
      if(modifyData){
         if(modifyData.name && modifyData.muscles && modifyData.category && modifyData.description && modifyData.email && modifyData.password && modifyData.dob && modifyData.objName && modifyData.objValue && modifyData.membershipDate)
         {
            axios.put(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Clients/${id}.json`,JSON.stringify(modifyData))
            .then((res)=>{
               console.log(res);
               alert('submit successfully....');
               history.push('/table-exercisetable-basic')
            })
            .catch((err)=>alert(err));
         }else{
            alert('Kindly fill the form properly');
         }
      }else{
            if(imgUpload){
              const imgRef = ref(storage,`clientImgs/${imgUpload.name}`);
              uploadBytes(imgRef,imgUpload)
              .then((res)=>{console.log(res);
            })
              .catch((err)=>console.log(err));
            }

         window.setTimeout(()=>{
            console.log(data);
            if(data.name && data.routine && data.startDate && data.endDate && data.dob && data.email && data.membershipDate && data.objName && data.objValue && data.password && data.pic)
            {
               axios.post('https://fitnessapp-607ec-default-rtdb.firebaseio.com/Clients.json',JSON.stringify(data))
               .then((res)=>{
                  alert('submit successfully....');
                  setData({name:'',routine:'',startDate:'',endDate:'',dob:'',email:'',membershipDate:'',objName:'',objValue:'',password:''});
               })
               .catch((err)=>alert(err));
            }else{
               alert('Kindly fill the form properly');
            }
         },3000)
     
      
      }
   }


   const fetchData=async(id)=>{
      var resClient =await axios.get(`https://fitnessapp-607ec-default-rtdb.firebaseio.com/Clients/${id}.json`);
      var resultClient=resClient.data;
      setModifyData(resultClient);
      console.log(resultClient);
      }
   
   React.useEffect(()=>{
    
      if(window.location.href.includes('/modify-client')){
         fetchData(id);
            }else{
         console.log('hello') ;
            }
   },[])
  

   const uploadImg=()=>{
      var ele = document.getElementById('imgFile');
      ele.click();
   }

   const onImageChange = (e) => {
      setData({...data,pic:e.target.files[0].name});
      setImgUpload(e.target.files[0]);
      const [file] = e.target.files;
      setImg(URL.createObjectURL(file));
    };


    return(
            <Fragment>
               <PageTItle activeMenu="New Client" motherMenu="Form" />
      
               <div className="row">
               <div className="card w75">
                        <div className="card-header">
                           <p className='title-descrip'>Here is a brief overview of activities from your clients.</p>
                        </div>
              {modifyData?(<div className="card-body colTwo5">
                  <div className="col-xl-12 col-lg-12">
                     
                        
                           <div className="basic-form">
                              <form onSubmit={(e) => e.preventDefault()}>
                                 <div className="form-group">
                                 <p className='input-label'>Name</p>
                                    <input
                                       type="text"
                                       name='name'
                                       value={modifyData.name}
                                       onChange={(e)=>setModifyData({...modifyData,name:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="enter name"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Date of Birth</p>
                                    <input
                                       type="date"
                                       name='dob'
                                       value={modifyData.dob}
                                       onChange={(e)=>setModifyData({...modifyData,dob:e.target.value})}
                                       className="form-control input-default input-border "
                                       // placeholder="enter name"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Routine</p>
                                    <input
                                       type="text"
                                       name='routine'
                                       value={modifyData.routine}
                                       onChange={(e)=>setModifyData({...ModifyData,routine:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="routine name"
                                    />
                                 </div>
                                 <div className='colTwo3'>
                                 <div className="form-group">
                                 <p className='input-label'>Start Date</p>
                                    <input
                                       type="date"
                                       name='startDate'
                                       value={modifyData.startDate}
                                       onChange={(e)=>setModifyData({...modifyData,startDate:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="start date"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>End Date</p>
                                    <input
                                       type="date"
                                       name='endDate'
                                       value={modifyData.endDate}
                                       onChange={(e)=>setModifyData({...modifyData,endDate:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="end date"
                                    />
                                 </div>
                                 </div>
                                 <div>
                                
    
                               </div>
                               <button className='mtb-3 submit-btn' onClick={submitHandler}>Submit</button>
                              </form>
                           </div>
                        </div>
                        <div className='col-xl-12 col-lg-12'>
                            <div className='img-div'>
                                <img className='w100 img-border' src={`https://firebasestorage.googleapis.com/v0/b/fitnessapp-607ec.appspot.com/o/clientImgs%2F${d.pic}?alt=media&token=5f34507a-528d-4abf-a21b-55a7cef5d205`} alt=''/>
                                <button className='modify-btn'>Modify</button>
                            </div>
                        </div>
                     </div>):(<div className="card-body colTwo5">
                  <div className="col-xl-12 col-lg-12">
                     
                        
                           <div className="basic-form">
                              <form onSubmit={(e) => e.preventDefault()}>
                                 <div className="form-group">
                                 <p className='input-label'>Name</p>
                                    <input
                                       type="text"
                                       name='name'
                                       value={data.name}
                                       onChange={(e)=>setData({...data,name:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="enter name"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Date of Birth</p>
                                    <input
                                       type="date"
                                       name='dob'
                                       value={data.dob}
                                       onChange={(e)=>setData({...data,dob:e.target.value})}
                                       className="form-control input-default input-border "
                                       // placeholder="enter name"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Member Since</p>
                                    <input
                                       type="date"
                                       name='membershipDate'
                                       value={data.membershipDate}
                                       onChange={(e)=>setData({...data,membershipDate:e.target.value})}
                                       className="form-control input-default input-border "
                                       // placeholder="enter name"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Email</p>
                                    <input
                                       type="email"
                                       name='email'
                                       value={data.email}
                                       onChange={(e)=>setData({...data,email:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="enter email"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Password</p>
                                    <input
                                       type="password"
                                       name='password'
                                       value={data.password}
                                       onChange={(e)=>setData({...data,password:e.target.value})}
                                       className="form-control input-default input-border "
                                       placeholder="enter password"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Routine</p>
                                    <input
                                       type="text"
                                       name='routine'
                                       value={data.routine}
                                       onChange={(e)=>setData({...data,routine:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="routine name"
                                    />
                                 </div>
                                 <div className='colTwo3'>
                                 <div className="form-group">
                                 <p className='input-label'>Start Date</p>
                                    <input
                                       type="date"
                                       name='startDate'
                                       value={data.startDate}
                                       onChange={(e)=>setData({...data,startDate:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="start date"
                                    />
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>End Date</p>
                                    <input
                                       type="date"
                                       name='endDate'
                                       value={data.endDate}
                                       onChange={(e)=>setData({...data,endDate:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="end date"
                                    />
                                 </div>
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Objective</p>
                                <select className='w100' name='objective' id='objective' value={data.objName} onChange={(e)=>setData({...data,objName:e.target.value})}>
                                <option value='none'>
                                    ---select---
                                 </option>                                 
                                 <option value='agua'>
                                    Agua
                                 </option>
                                 <option value='biceps'>
                                   Biceps
                                 </option>
                                 <option value='grasa'>
                                    Grasa
                                 </option>
                                 <option value='peso'>
                                    Peso
                                 </option>
                                 <option value='pierna'>
                                    Pierna
                                 </option>
                                 <option value='tripa'>
                                    Tripa
                                 </option>
                                 <option value='musculo'>
                                    Musculo
                                 </option>
                                </select>
                                 </div>
                                 <div className="form-group">
                                 <p className='input-label'>Objective Value</p>
                                    <input
                                       type="number"
                                       name='objValue'
                                       value={data.objValue}
                                       onChange={(e)=>setData({...data,objValue:e.target.value})}
                                       className="form-control input-default input-border"
                                       placeholder="objective value"
                                    />
                                 </div>
                              
                                 <div>
                          
                               </div>
                               <button className='mtb-3 submit-btn' onClick={submitHandler}>Submit</button>
                              </form>
                           </div>
                        </div>
                        <div className='col-xl-12 col-lg-12'>
                            <div className='img-div'>
                                <img className='w100 img-border ' src={img} alt=''/>
                                <button className='modify-btn' onClick={uploadImg}>Modify IMG</button>
                            </div>
                            <input style={{display:'none'}} type='file' id='imgFile' onChange={onImageChange}/>
                        </div>
                     </div>)
}
                  </div>
                  <div className="clients-statics stat-sec">
<h3 className="title">
    Statistics
</h3>
                  </div>
                  </div>
                  </Fragment>
    )
}


export default NewClientForm;
