export const ANGLE_PICKER_PROP_TYPES = {
	angle: number.isRequired,
	setAngle: func.isRequired,
	size: number,
	snap: number
};

export const GRADIENT_PICKER_POPOVER_PROP_TYPES = {
	...GRADIENT_PICKER_PROP_TYPES,
	...ANGLE_PICKER_PROP_TYPES,
	showAnglePicker: bool,
	open: bool.isRequired,
	setOpen: func.isRequired,
	trigger: func,
};

/**
 * The drag supported events
 * Object<String>
 */
export const EVENTS = {
	MOUSEDOWN: 'mousedown',
	MOUSEMOVE: 'mousemove',
	MOUSEUP: 'mouseup',
	TOUCHSTART: 'touchstart',
	TOUCHMOVE: 'touchmove',
	TOUCHEND: 'touchend'
};

import { useState, useEffect } from 'react';
import { noop } from '../../../lib';
import { EVENTS } from './constants';

const DRAG_HANDLERS = {
	MOUSE: {
		stop: (e) => {
			e.preventDefault();
			e.stopPropagation();
		},
		coordinates: ({ clientX, clientY }) => ({ clientX, clientY }),
		dragEvent: { name: EVENTS.MOUSEMOVE },
		dragEndEvent: { name: EVENTS.MOUSEUP }
	},
	TOUCH: {
		stop: noop,
		coordinates: (e) => {
			const [touch] = e.touches;
			return { clientX: touch.clientX, clientY: touch.clientY };
		},
		dragEvent: { name: EVENTS.TOUCHMOVE, options: { cancelable: true, passive: true } },
		dragEndEvent: { name: EVENTS.TOUCHEND }
	}
};

const isTouch = (e) => e.type === EVENTS.TOUCHSTART;

const useDragging = ({ onDragStart = noop, onDrag, onDragEnd = noop }) => {
	const [context, setContext] = useState({});
	const [dragging, setDragging] = useState(false);

	const dragHandler = (e) => {
		const handler = isTouch(e) ? DRAG_HANDLERS.TOUCH : DRAG_HANDLERS.MOUSE;

		handler.stop(e);

		if (!e.button) activate(e, handler);
	};

	const activate = (e, handler) => {
		setDragging(true);
		context.handler = handler;

		onDragStart(handler.coordinates(e));
	};

	const deactivate = () => {
		setDragging(false);

		onDragEnd(context.change);
		setContext({});
	};

	const handleDrag = (e) => {
		const { handler } = context;
		if (!dragging) return;

		context.change = onDrag(handler.coordinates(e));
	};

	useEffect(() => {
		const { handler } = context;
		if (!handler) return;

		const { dragEvent, dragEndEvent } = handler;

		if (dragging) {
			document.addEventListener(dragEvent.name, handleDrag, dragEndEvent.options);
			document.addEventListener(dragEndEvent.name, deactivate);
		}

		return () => {
			document.removeEventListener(dragEvent.name, handleDrag, dragEndEvent.options);
			document.removeEventListener(dragEndEvent.name, deactivate);
		};
	}, [dragging]);

	return [
		dragHandler,
		activate,
		deactivate
	];
};

export default useDragging;

import React, { useRef } from 'react';
import { ANGLE_PICKER_PROP_TYPES } from '../propTypes';
import useDragging from '../hooks/useDragging';
import {
	centerOffset,
	clampAngle,
	snapAngle,
	pointDegrees
} from '../../lib';
import './index.css';

const AnglePicker = ({ angle, setAngle, size = 48, snap = 5 }) => {
	const pickerRef = useRef();
	const sizeStyle = { height: size, width: size };

	const onAngleChange = ({ clientX, clientY }, useSnap = false) => {
		const center = centerOffset(pickerRef.current);
		const degrees = pointDegrees(clientX, clientY, center);

		const clamped = clampAngle(degrees);
		const angle = useSnap ? snapAngle(clamped, snap) : clamped;

		setAngle(angle);
		return angle;
	};

	const [drag] = useDragging({
		onDragStart: (e) => onAngleChange(e, true),
		onDrag: onAngleChange,
		onDragEnd: (angle) => {
			if (!angle) return;
			const snappedAngle = snapAngle(angle, snap);

			setAngle(snappedAngle);
		}
	});

	return (
		<div className="ap" ref={pickerRef} onMouseDown={drag} onTouchStart={drag} style={sizeStyle}>
			<span className="apc" style={{ transform: `rotate(${angle}deg)`, height: size }}>
				<i className="aph"/>
			</span>
		</div>
	);
};

AnglePicker.propTypes = ANGLE_PICKER_PROP_TYPES;

export default AnglePicker;

import { useState } from 'react';
import useDragging from '../../hooks/useDragging';

/**
 * Limits a client drag movement within given min / max
 * @param {Number} offset - The current clientX
 * @param {Number} min - Min boundary
 * @param {Number} max - Max boundary
 * @returns {Number}
 */
const limitPos = (offset, min, max) => Math.max(Math.min(offset, max), min);

const getColorStopRefTop = (ref) => {
	if (!ref.current) return 0;
	return ref.current.getBoundingClientRect().top;
};

const useStopDragging = ({ limits, stop, initialPos, colorStopRef, onPosChange, onDragStart, onDragEnd, onDeleteColor}) => {
	const [posStart, setPosStart] = useState(initialPos);

	const handleDrag = ({ clientX, clientY }) => {
		const { id, offset } = stop;
		const { min, max } = limits;

		// Removing if out of drop limit on Y axis.
		const top = getColorStopRefTop(colorStopRef);
		if (Math.abs(clientY - top) > limits.drop) {
			//deactivate();
			return onDeleteColor(id);
		}

		// Limit movements
		const dragOffset = offset - posStart;
		const limitedPos = limitPos(dragOffset + clientX, min, max);

		onPosChange({ id, offset: limitedPos });
	};

	const [drag] = useDragging({
		onDragStart: ({ clientX }) => {
			setPosStart(clientX);

			onDragStart(stop.id);
		},
		onDrag: handleDrag,
		onDragEnd: () => onDragEnd(stop.id)
	});

	return [
		drag,
	];
};

export default useStopDragging;

import React, { useRef } from 'react';
import { noop } from '../../lib';
import { STOP_PROP_TYPES } from '../propTypes';
import useStopDragging from './hooks/useStopDragging';
import './index.css';

const ColorStop = ({ stop, limits, onPosChange, onDeleteColor, onDragStart = noop, onDragEnd = noop}) => {
	const colorStopRef = useRef();
	const [drag] = useStopDragging({
		stop,
		limits,
		onPosChange,
		onDragStart,
		onDragEnd,
		onDeleteColor,
		colorStopRef
	});

	const { offset, color, isActive } = stop;

	return (
		<div className={isActive ? 'cs active' : 'cs'}
			ref={colorStopRef}
			style={{ left: offset }}
			onMouseDown={drag}
			onTouchStart={drag}>
			<div style={{ backgroundColor: color }}/>
		</div>
	);
};

ColorStop.propTypes = STOP_PROP_TYPES;

export default ColorStop;

import React from 'react';
import ColorStop from '../ColorStop/index';
import { STOPS_HOLDER_PROP_TYPES } from '../propTypes';

const getStopsHolderStyle = (width, disabled) => ({
	width,
	height: 17,
	position: 'relative',
	cursor: disabled ? 'default' : 'crosshair'
});

const ColorStopsHolder = ({ width, stops, disabled = false, onAddColor, ...rest }) => {

	const handleColorAdd = (e) => {
		e.preventDefault();

		if (e.button) return;

		const offset = e.clientX - e.target.getBoundingClientRect().left;
		onAddColor({ offset });
	};

	return (
		<div className="csh" style={getStopsHolderStyle(width, disabled)} onMouseDown={handleColorAdd}>
			{stops.map(stop =>
				<ColorStop key={stop.id} stop={stop} {...rest} />
			)}
		</div>
	);
};

ColorStopsHolder.propTypes = STOPS_HOLDER_PROP_TYPES;

export default ColorStopsHolder;

import React, { useMemo } from 'react';
import { sortPalette } from '../../lib';
import { PALETTE_PROP_TYPES } from '../propTypes';

const generateGradientId = () => '' + Math.random().toString(36).substr(2, 9);

const Palette = ({ palette, width, height }) => {
	const sortedPalette = sortPalette(palette);
	const gradientId = useMemo(generateGradientId, [palette.length]);

	return (
		<div className="palette" style={{ width, height }}>
			<svg width="100%" height="100%">
				<defs>
					<linearGradient id={gradientId} x1="0" y1="0.5" x2="1" y2="0.5"> {
						sortedPalette.map(({ id, offset, color, opacity = 1 }) =>
							<stop key={id} offset={offset} style={{ stopColor: color, stopOpacity: opacity }}/>
						)}
					</linearGradient>
				</defs>
				<rect x="0" y="0" width="100%" height="100%" fill={`url(#${gradientId})`}/>
			</svg>
		</div>
	);
};

Palette.propTypes = PALETTE_PROP_TYPES;

export default Palette;

